DROP TABLE IF EXISTS `tree_lr`;
CREATE TABLE `tree_lr` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `pid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '父亲id',
  `name` varchar(30) NOT NULL DEFAULT '' COMMENT '姓名',
