  `R` int(6) DEFAULT NULL COMMENT '右值',
  `dorder` int(6) DEFAULT NULL COMMENT '左右值移动定点',
  `sex` varchar(255) DEFAULT NULL COMMENT '性别',
  `wname` varchar(255) DEFAULT NULL COMMENT '妻子',
  `dad` varchar(255) DEFAULT NULL COMMENT '父亲',
  `gdad` varchar(255) DEFAULT NULL COMMENT '祖父',
  `mother` varchar(255) DEFAULT NULL COMMENT '母亲',
  `dc` smallint(6) unsigned DEFAULT NULL COMMENT '世代',
  `zibei` varchar(255) DEFAULT NULL COMMENT '辈份',
  `rank` varchar(255) DEFAULT NULL COMMENT '排行',
  `line` varchar(6) NOT NULL DEFAULT '' COMMENT '自然排行',
  `brother` varchar(255) DEFAULT NULL COMMENT '兄弟',
  `sisters` varchar(255) DEFAULT NULL COMMENT '姊妹',
  `son` varchar(255) DEFAULT NULL COMMENT '儿子',
  `daughter` varchar(255) DEFAULT NULL COMMENT '女儿',
